/**
* @return array adGroups, onedim array of objects
* @throws error ExtraSpaceInAdGroup_Error
*/
FeedHandler.prototype.getAdGroupObjects = function() {
  var adGroupObjects = [];
  Logger.log("Building adgroup objects. Total input : " + this.feedContent.length);

  var KEYWORD_VALIDATION_LOG = [];
  var historicalData = {};

  if(INPUT_SOURCE_MODE === "ADBUILD") {
    if(NEW_CAMPAIGN_CONFIG.useQueryData.filterByQueries == 1) historicalData = this.getHistoricalQueryData();

    var prevalidatedKeywords = this.storageHandler.selectKeywordsByStatus("prevalidatedKeywords", true);
    var discardedKeywords = this.storageHandler.selectKeywordsByStatus("prevalidatedKeywords", false);
    var maxLimit = (this.feedContent.length - prevalidatedKeywords.length - discardedKeywords.length) > 500 ? (prevalidatedKeywords.length + discardedKeywords.length + 500) : this.feedContent.length;

  }
  var maxLimit = typeof maxLimit == "undefined" ?  this.feedContent.length : maxLimit;


  for(var i=1;i<maxLimit;i++){ //
    var listItem = this.feedContent[i];

    // Check AdGroup Names or extra space at beginning
    if(listItem[this.columnMapper["target ad group"]].charAt[0] === " ") {
      throw new Error("ExtraSpaceInAdGroup_Error: Your adgroup name '" + listItem[this.columnMapper["target ad group"]] + "' contains a space at the beginning. Please remove from feed, this will cause issues.");
    }

    // converts a string into an upperCaseFirstLetter version
    var ucFirstAdGroup = this.upperCaseFirst(listItem[this.columnMapper["target ad group"]]);
    var ucFirstKeyword = this.upperCaseFirst(listItem[this.columnMapper["keyword_full (text)"]]);

    // var colorValue = typeof listItem[this.columnMapper["color (text)"]] !== "undefined" ? listItem[this.columnMapper["color (text)"]] : "";
    var minPriceValue = typeof listItem[this.columnMapper["price_min (number)"]] !== "undefined" ? listItem[this.columnMapper["price_min (number)"]] : listItem[this.columnMapper["price_min (text)"]];

    adGroupObject = {
      adGroup : ucFirstAdGroup,
      campaign : listItem[this.columnMapper["target campaign"]],
      headline : listItem[this.columnMapper["headline (text)"]],
      kwWithUnderscore : ucFirstKeyword,
      discount : listItem[this.columnMapper["discount (number)"]],
      minPrice : minPriceValue,
      saleItems : listItem[this.columnMapper["sale_item_count (number)"]],
      aggregationType : listItem[this.columnMapper["aggregation_type (text)"]],
      brand : listItem[this.columnMapper["brand (text)"]],
      category : listItem[this.columnMapper["category (text)"]],
      gender : listItem[this.columnMapper["gender (text)"]],
      /*color : colorValue*/
    };

    if(EXTRA_COLUMNS.length > 0 && EXTRA_COLUMN_OBJECTVALUES){
      for(var j=0; j<EXTRA_COLUMNS.length; j++){
      	if(EXTRA_COLUMNS[j].toLowerCase().indexOf('url') > -1){
      		adGroupObject[EXTRA_COLUMN_OBJECTVALUES[j].toLowerCase()] = listItem[this.columnMapper[EXTRA_COLUMNS[j].toLowerCase()]];
      	} else {
      		adGroupObject[EXTRA_COLUMN_OBJECTVALUES[j]] = listItem[this.columnMapper[EXTRA_COLUMNS[j]]];
      	}

      }
    } // END extra columns

    if(adGroupObject.campaign != this.campaignName) continue;

    // Push All adgroups in feed to adgroup Objects
    if(INPUT_SOURCE_MODE === "SQA") {
      adGroupObjects.push(adGroupObject);
      continue;
    }

    if(NEW_CAMPAIGN_CONFIG.useQueryData.filterByQueries == 0) adGroupObjects.push(adGroupObject);

    if(NEW_CAMPAIGN_CONFIG.useQueryData.filterByQueries == 1) {

      // Only push adgroups if query is found in Google Suggest or minKPI condition is satisfied
      var cleanedKeyword = adGroupObject.kwWithUnderscore.replace(/_/g," ").toLowerCase();
      var adGroupPushed = 0;

      // Early skip if keyword already discarded
      if(discardedKeywords.indexOf(cleanedKeyword) != -1) continue;

      var keywordValidationLogObject = {
        "id" : cleanedKeyword,
        "validationStatus" : true,
        "adGroupName" : adGroupObject.adGroup
      };

      // Case I: Found in validatedKeywords Cache
      if(prevalidatedKeywords.indexOf(cleanedKeyword) != -1) {
        // Logger.log("Case I: Found in validatedKeywords Cache | " + cleanedKeyword);
        adGroupObjects.push(adGroupObject);
        adGroupPushed = 1;
      }

      // Case II: Found in Google Suggest
      if(adGroupPushed == 0) {
        if (this.foundInGoogleSuggest(cleanedKeyword) == 1) {
          Logger.log("Case II: Found in Google Suggest | " + cleanedKeyword);
          adGroupObjects.push(adGroupObject);
          adGroupPushed = 1;

          keywordValidationLogObject.validationType = "suggest";
          KEYWORD_VALIDATION_LOG.push(keywordValidationLogObject);
        }
      }

      // Case III: Check historical account queries
      if(adGroupPushed == 0) {
        Logger.log("Case III: Check historical account queries | " + cleanedKeyword);
        if(this.checkIfKpiLevelReached(cleanedKeyword, historicalData) == 0) {
          keywordValidationLogObject.validationStatus = false;
          keywordValidationLogObject.validationType = "none";
          KEYWORD_VALIDATION_LOG.push(keywordValidationLogObject);
        }
        if(this.checkIfKpiLevelReached(cleanedKeyword, historicalData) == 1) {
          adGroupObjects.push(adGroupObject);
          keywordValidationLogObject.validationType = "accountQueries";
          KEYWORD_VALIDATION_LOG.push(keywordValidationLogObject);
        }
      } // END case III
    }

    var minutesRemaining = AdsApp.getExecutionInfo().getRemainingTime()/60;
    if (minutesRemaining < 5) {Logger.log("Execution time past twentyfive minutes. Stopping execution..."); break;}

  } // END For loop

  Logger.log("New Cache Entries : " + JSON.stringify(KEYWORD_VALIDATION_LOG));
  if(KEYWORD_VALIDATION_LOG.length > 0 && INPUT_SOURCE_MODE == "ADBUILD") this.storageHandler.writeRows(KEYWORD_VALIDATION_LOG);
  Logger.log("validated adGroupObjects : " + adGroupObjects.length + " for campaign " + this.campaignName); Logger.log(" ");
  return adGroupObjects;
};


