    var exactKw = '[' + adGroupObject.kwWithUnderscore.split("_").join(" ").replace(/\//g,' ') + ']';
    var reverseExactKw = '[' + adGroupObject.kwWithUnderscore.split("_").reverse().join(" ").replace(/\//g,' ') + ']';
 
